import{_ as e,o as a,c as r,a as t}from"./app.fe1fa928.js";const o={},n=t('<p>\u5728HTTP\u534F\u8BAE\u4E2D\uFF0C\u8BF7\u6C42\u5934\u626E\u6F14\u7740\u4F20\u9012\u8BF7\u6C42\u76F8\u5173\u4FE1\u606F\u7684\u91CD\u8981\u89D2\u8272\u3002\u7136\u800C\uFF0C\u4F60\u662F\u5426\u6CE8\u610F\u8FC7HTTP\u8BF7\u6C42\u5934\u7684\u5B57\u6BB5\u540D\u4E0E\u4F60\u5B9A\u4E49\u7684\u5B57\u6BB5\u540D\u53EF\u80FD\u4F1A\u4E0D\u4E00\u6837\u5462\uFF1F</p><h2 id="\u7B80\u8FF0" tabindex="-1"><a class="header-anchor" href="#\u7B80\u8FF0" aria-hidden="true">#</a> \u7B80\u8FF0</h2><p>HTTP \u6807\u5934\uFF08header\uFF09\u5141\u8BB8\u5BA2\u6237\u7AEF\u548C\u670D\u52A1\u5668\u901A\u8FC7 HTTP \u8BF7\u6C42\uFF08request\uFF09\u6216\u8005\u54CD\u5E94\uFF08response\uFF09\u4F20\u9012\u9644\u52A0\u4FE1\u606F\u3002\u4E00\u4E2A HTTP \u6807\u5934\u7531\u5B83\u7684\u540D\u79F0\uFF08\u4E0D\u533A\u5206\u5927\u5C0F\u5199\uFF09\u540E\u8DDF\u968F\u4E00\u4E2A\u5192\u53F7\uFF08:\uFF09\uFF0C\u5192\u53F7\u540E\u8DDF\u968F\u5B83\u5177\u4F53\u7684\u503C\u3002\u8BE5\u503C\u4E4B\u524D\u7684\u7A7A\u683C\u4F1A\u88AB\u5FFD\u7565\u3002</p><p>\u5728HTTP\u534F\u8BAE\u89C4\u8303\u4E2D\uFF0C\u5E76\u6CA1\u6709\u660E\u786E\u89C4\u5B9A\u8BF7\u6C42\u5934\u7684\u9996\u5B57\u6BCD\u5FC5\u987B\u5927\u5199\u3002\u901A\u5E38\u60C5\u51B5\u4E0B\uFF0CHTTP\u8BF7\u6C42\u5934\u662F\u4E0D\u533A\u5206\u5927\u5C0F\u5199\u7684\uFF0C\u4E5F\u5C31\u662F\u8BF4\uFF0CContent-Type\u548Ccontent-type\u5728HTTP\u534F\u8BAE\u4E2D\u88AB\u89C6\u4E3A\u76F8\u540C\u7684\u5934\u5B57\u6BB5\u3002\u7136\u800C\uFF0C\u6839\u636E\u60EF\u4F8B\uFF0C\u901A\u5E38\u5EFA\u8BAE\u5C06\u8BF7\u6C42\u5934\u7684\u9996\u5B57\u6BCD\u5927\u5199\uFF0C\u4EE5\u63D0\u9AD8\u53EF\u8BFB\u6027\u548C\u4E00\u81F4\u6027\u3002</p><h2 id="\u53C2\u8003\u6587\u732E" tabindex="-1"><a class="header-anchor" href="#\u53C2\u8003\u6587\u732E" aria-hidden="true">#</a> \u53C2\u8003\u6587\u732E</h2><ul><li>HTTP/1.x</li></ul><p>\u6240\u6709\u8BF7\u6C42\u5934\u4E2D\u7684\u5B57\u6BB5\u662F\u5927\u5C0F\u5199\u4E0D\u654F\u611F\u7684</p><p><a href="https://www.rfc-editor.org/rfc/rfc7230#section-3.2" target="_blank" rel="noopener noreferrer">!RFC7230 3.2\u8282</a></p><blockquote><p>Each header field consists of a case-insensitive field name followed by a colon (&quot; : &quot;), optional leading whitespace, the field value, and optional trailing whitespace.</p></blockquote><ul><li>HTTP/2</li></ul><p>\u6CBF\u7528\u4E86HTTP/1.x\u7684\u6807\u51C6\uFF0C\u5E76\u4E14\u4F1A<code>\u5F3A\u5236</code>\u8F6C\u6362\u4E3A\u5168\u5C0F\u5199\u5B57\u6BCD</p><p><a href="https://www.rfc-editor.org/rfc/rfc7540.html#section-8.1.2" target="_blank" rel="noopener noreferrer">!RFC7540 8.1.2\u8282</a></p><blockquote><p>Just as in HTTP/1.x, header field names are strings of ASCII characters that are compared in a case-insensitive fashion. However, header field names MUST be converted to lowercase prior to their encoding in HTTP/2.</p></blockquote><ul><li>\u963F\u91CC\u4E91\u51FD\u6570\u8BA1\u7B97\u76F8\u5173\u6587\u6863</li></ul><p><a href="https://help.aliyun.com/zh/functioncompute/why-does-the-first-letter-of-header-key-become-uppercase-when-using-http-trigger-call" target="_blank" rel="noopener noreferrer">!\u4F7F\u7528HTTP\u89E6\u53D1\u5668\u8C03\u7528\u65F6\uFF0CHeader key\u9996\u5B57\u6BCD\u4E3A\u4EC0\u4E48\u53D8\u6210\u4E86\u5927\u5199\uFF1F</a></p><blockquote><p>HTTP Header\u8BF7\u6C42\u5934\u4EE5\u952E\u503C\u5BF9\u7684\u5F62\u5F0F\u663E\u793A\u3002\u6839\u636EHTTP\u6807\u51C6\uFF0CHeader\u7684\u952E\u662F\u5927\u5C0F\u5199\u4E0D\u654F\u611F\u7684\u3002 \u5F53\u4F7F\u7528HTTP\u89E6\u53D1\u5668\u8C03\u7528\u6807\u51C6\u8FD0\u884C\u65F6\uFF0C\u51FD\u6570\u8BA1\u7B973.0\u7248\u4F1A\u5C06HTTP\u8BF7\u6C42\u8F6C\u6362\u6210HTTP\u89E6\u53D1\u5668\u7684Event\u683C\u5F0F\uFF0C\u5728\u8F6C\u6362HTTP Header\u65F6\uFF0C\u4F1A\u57FA\u4E8EGolang\u7684net/http\u6807\u51C6\u5E93\uFF0C\u5C06HTTP\u8BF7\u6C42Header\u7684\u952E\u8FDB\u884C\u89C4\u8303\u5316\u3002 \u89C4\u8303\u5316\u7684\u539F\u5219\u662F\u5C06\u952E\u7684\u9996\u5B57\u6BCD\u548C\u4EFB\u4F55\u8FDE\u5B57\u7B26\u540E\u7684\u5B57\u6BCD\u8F6C\u6210\u5927\u5199\uFF0C\u5176\u4F59\u5B57\u6BCD\u8F6C\u6362\u4E3A\u5C0F\u5199\u3002\u4F8B\u5982\uFF0C&quot;accept-encoding&quot; \u89C4\u8303\u5316\u540E\u662F &quot;Accept-Encoding&quot;\u3002</p></blockquote><h2 id="\u5DEE\u5F02" tabindex="-1"><a class="header-anchor" href="#\u5DEE\u5F02" aria-hidden="true">#</a> \u5DEE\u5F02</h2><p>\u65E2\u7136HTTP/1.x\u534F\u8BAE\u89C4\u8303\u4E2D\u5B9A\u4E49\u8BF7\u6C42\u5934\u4E2D\u7684\u5B57\u6BB5\u662F\u5927\u5C0F\u5199\u4E0D\u654F\u611F\u7684\uFF0C\u90A3\u4E3A\u4EC0\u4E48\u4F1A\u5448\u73B0\u51FA\u9996\u5B57\u6BCD\u5927\u5199\u7684\u60C5\u51B5\u5462\uFF1F</p><ul><li><p>\u89C4\u8303\u6027\u548C\u4E00\u81F4\u6027: \u9996\u5B57\u6BCD\u5927\u5199\u7684\u683C\u5F0F\u4F7F\u5F97HTTP\u8BF7\u6C42\u5934\u5B57\u6BB5\u66F4\u5177\u53EF\u8BFB\u6027\u548C\u4E00\u81F4\u6027\uFF0C\u6709\u52A9\u4E8E\u5F00\u53D1\u4EBA\u5458\u66F4\u5BB9\u6613\u7406\u89E3\u548C\u8BC6\u522B\u5B57\u6BB5</p></li><li><p>\u6613\u4E8E\u533A\u5206: HTTP\u8BF7\u6C42\u5934\u5B57\u6BB5\u7684\u9996\u5B57\u6BCD\u5927\u5199\u6709\u52A9\u4E8E\u5C06\u5B57\u6BB5\u540D\u4E0E\u5B57\u6BB5\u503C\u660E\u786E\u533A\u5206\u5F00\u6765\uFF0C\u4ECE\u800C\u51CF\u5C11\u53EF\u80FD\u7684\u6DF7\u6DC6</p></li><li><p>\u907F\u514D\u5927\u5C0F\u5199\u654F\u611F\u95EE\u9898: HTTP\u534F\u8BAE\u89C4\u8303\u4E2D\u89C4\u5B9A\u4E86\u8BF7\u6C42\u5934\u5B57\u6BB5\u540D\u662F\u4E0D\u533A\u5206\u5927\u5C0F\u5199\u7684\uFF0C\u4F46\u5B9E\u9645\u4E0A\uFF0C\u8BB8\u591A\u670D\u52A1\u5668\u548C\u8F6F\u4EF6\u5B9E\u73B0\u4F1A\u533A\u5206\u5B57\u6BB5\u540D\u7684\u5927\u5C0F\u5199\u3002\u56E0\u6B64\uFF0C\u91C7\u7528\u9996\u5B57\u6BCD\u5927\u5199\u7684\u683C\u5F0F\u53EF\u4EE5\u907F\u514D\u7531\u4E8E\u5927\u5C0F\u5199\u654F\u611F\u6027\u5E26\u6765\u7684\u95EE\u9898</p></li></ul><p>HTTP\u8BF7\u6C42\u5934\u7684\u5B57\u6BB5\u540D\u9996\u5B57\u6BCD\u5927\u5199\u7684\u8BBE\u8BA1\u5E26\u6765\u4E86\u8BF8\u591A\u597D\u5904\u3002\u9996\u5148\uFF0C\u5B83\u63D0\u9AD8\u4E86\u53EF\u8BFB\u6027\uFF0C\u4F7F\u5F97\u8BF7\u6C42\u5934\u66F4\u6613\u4E8E\u7406\u89E3\u548C\u89E3\u6790\u3002\u5176\u6B21\uFF0C\u8FD9\u79CD\u8BBE\u8BA1\u4FDD\u6301\u4E86\u534F\u8BAE\u7684\u4E00\u81F4\u6027\uFF0C\u4F7F\u5F97\u4E0D\u540C\u5B9E\u73B0\u4E4B\u95F4\u66F4\u6613\u4E8E\u4EA4\u4E92\u548C\u517C\u5BB9\u3002\u6B64\u5916\uFF0C\u5927\u5199\u5B57\u6BCD\u7684\u4F7F\u7528\u4E5F\u7B26\u5408\u4E86HTTP\u534F\u8BAE\u8BBE\u8BA1\u7684\u6574\u4F53\u98CE\u683C\u548C\u89C4\u8303\u3002</p><p>\u8BA9\u6211\u4EEC\u6765\u770B\u51E0\u4E2A\u5E38\u89C1\u7684HTTP\u8BF7\u6C42\u5934\u5B57\u6BB5\uFF1AContent-Type\u3001User-Agent\u3002\u8FD9\u4E9B\u5B57\u6BB5\u540D\u4EE5\u5927\u5199\u5B57\u6BCD\u5F00\u5934\uFF0C\u4E0E\u5176\u4ED6\u5B57\u6BB5\u503C\u660E\u663E\u533A\u5206\u5F00\u6765\u3002\u76F8\u6BD4\u4E4B\u4E0B\uFF0C\u5982\u679C\u5B57\u6BB5\u540D\u91C7\u7528\u5C0F\u5199\u5B57\u6BCD\u6216\u5927\u5C0F\u5199\u6DF7\u5408\uFF0C\u53EF\u80FD\u4F1A\u5BFC\u81F4\u6DF7\u6DC6\u548C\u4E0D\u4E00\u81F4\u6027\u3002</p><h2 id="\u7ED3\u8BBA" tabindex="-1"><a class="header-anchor" href="#\u7ED3\u8BBA" aria-hidden="true">#</a> \u7ED3\u8BBA</h2><p>HTTP\u8BF7\u6C42\u5934\u4E3A\u4EC0\u4E48\u662F\u9996\u5B57\u6BCD\u5927\u5199\u7684\uFF0C\u8FD9\u4E2A\u95EE\u9898\u5E76\u975E\u53EA\u662F\u4E00\u79CD\u89C4\u8303\uFF0C\u66F4\u662F\u4E3A\u4E86\u63D0\u9AD8\u534F\u8BAE\u7684\u53EF\u8BFB\u6027\u3001\u4E00\u81F4\u6027\u548C\u6613\u7528\u6027\u3002\u901A\u8FC7\u4FDD\u6301\u5B57\u6BB5\u540D\u7684\u9996\u5B57\u6BCD\u5927\u5199\uFF0CHTTP\u534F\u8BAE\u5728\u8BBE\u8BA1\u4E0A\u66F4\u52A0\u6E05\u6670\u548C\u4F18\u96C5\u3002</p>',23),i=[n];function c(l,p){return a(),r("div",null,i)}var d=e(o,[["render",c],["__file","2024-08-07-Header.html.vue"]]);export{d as default};
